angular.module('test1', ['test2'])

	.config(function ($stateProvider, $httpProvider) {

		$stateProvider
			.state('index1.entry', {
				url: '/entry',
				template: '<span>卡卡西 我爱罗</span>'
			});

		$httpProvider.interceptors.push(function () {

			return {

				request: function (configs) {
					console.log(11);
					return configs;
				}

			};

		});

	})

	.controller('index1Ctrl', function ($scope, $state, $http) {

		$scope.click = function ($event) {
			console.log($event);
			$http.get('/ajax');
			$state.go('index1.entry');
		};



	})

	.run(function () {

		console.log('test1 loaded');

	});